from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n: int) -> int:
            if n < 1:
                return 0
            digits = list(map(int, str(n)))
            length = len(digits)

            @lru_cache(None)
            def dp(pos: int, tight: bool, started: bool, has_zero: bool, sum_digits: int, e2: int, e3: int, e5: int, e7: int) -> int:
                if pos == length:
                    if not started:
                        return 0
                    if has_zero:
                        return 1
                    product = (2 ** e2) * (3 ** e3) * (5 ** e5) * (7 ** e7)
                    return 1 if sum_digits > 0 and product % sum_digits == 0 else 0

                limit = digits[pos] if tight else 9
                total = 0

                for d in range(limit + 1):
                    new_tight = tight and (d == limit)
                    if not started:
                        if d == 0:
                            total += dp(pos + 1, new_tight, False, False, 0, 0, 0, 0, 0)
                        else:
                            new_e2 = new_e3 = new_e5 = new_e7 = 0
                            temp = d
                            while temp % 2 == 0:
                                new_e2 += 1
                                temp //= 2
                            while temp % 3 == 0:
                                new_e3 += 1
                                temp //= 3
                            while temp % 5 == 0:
                                new_e5 += 1
                                temp //= 5
                            while temp % 7 == 0:
                                new_e7 += 1
                                temp //= 7
                            total += dp(pos + 1, new_tight, True, False, d, new_e2, new_e3, new_e5, new_e7)
                    else:
                        if has_zero:
                            total += dp(pos + 1, new_tight, True, True, sum_digits + d, 0, 0, 0, 0)
                        else:
                            if d == 0:
                                total += dp(pos + 1, new_tight, True, True, sum_digits, 0, 0, 0, 0)
                            else:
                                new_e2, new_e3, new_e5, new_e7 = e2, e3, e5, e7
                                temp = d
                                while temp % 2 == 0:
                                    new_e2 += 1
                                    temp //= 2
                                while temp % 3 == 0:
                                    new_e3 += 1
                                    temp //= 3
                                while temp % 5 == 0:
                                    new_e5 += 1
                                    temp //= 5
                                while temp % 7 == 0:
                                    new_e7 += 1
                                    temp //= 7
                                total += dp(pos + 1, new_tight, True, False, sum_digits + d, new_e2, new_e3, new_e5, new_e7)

                return total

            return dp(0, True, False, False, 0, 0, 0, 0, 0)

        return count_beautiful(r) - count_beautiful(l - 1)

