        @lru_cache(maxsize=None)
        def dp(pos, sum_d, prod, started, tight):
            if pos == n:
                return int(started and sum_d and prod % sum_d == 0)
            lim = int(s[pos]) if tight else 9
            res = 0
            for d in range(lim + 1):
                ntight = tight and (d == lim)
                if not started:
                    if d == 0:
                        res += dp(pos + 1, 0, 1, False, ntight)
                    else:
                        res += dp(pos + 1, d, d, True, ntight)
                else:
                    res += dp(pos + 1, sum_d + d, prod * d, True, ntight)
            return res
        return dp(0, 0, 1, False, True)

