            @cache
            def dp(pos, tight, started, has_zero, prod, s_sum):
                if pos == n:
                    if not started:
                        return 0
                    if has_zero:
                        return 1
                    return 1 if s_sum != 0 and prod % s_sum == 0 else 0

                limit = int(s[pos]) if tight else 9
                total = 0
                for d in range(limit + 1):
                    new_tight = tight and (d == limit)
                    if not started:
                        if d == 0:
                            total += dp(pos + 1, new_tight, False, False, 1, 0)
                        else:
                            total += dp(pos + 1, new_tight, True, False, d, d)
                    else:
                        if not has_zero:
                            if d == 0:
                                total += dp(pos + 1, new_tight, True, True, 0, s_sum)
                            else:
                                total += dp(pos + 1, new_tight, True, False, prod * d, s_sum + d)
                        else:
                            total += dp(pos + 1, new_tight, True, True, 0, s_sum + d)
                return total

            return dp(0, True, False, False, 1, 0)

        return count(r) - count(l - 1)

