#include <bits/stdc++.h>
using namespace std;

struct DPState {
    int idx, tight, started, sum;
    long long prod;
    bool operator==(const DPState &o) const {
        return idx == o.idx && tight == o.tight && started == o.started &&
               sum == o.sum && prod == o.prod;
    }
};

struct DPStateHash {
    size_t operator()(const DPState &s) const {
        size_t a = hash<int>()(s.idx);
        size_t b = hash<int>()(s.tight);
        size_t c = hash<int>()(s.started);
        size_t d = hash<int>()(s.sum);
        size_t e = hash<long long>()(s.prod);
        size_t res = a;
        res ^= b + 0x9e3779b97f4a7c15ULL + (res << 6) + (res >> 2);
        res ^= c + 0x9e3779b97f4a7c15ULL + (res << 6) + (res >> 2);
        res ^= d + 0x9e3779b97f4a7c15ULL + (res << 6) + (res >> 2);
        res ^= e + 0x9e3779b97f4a7c15ULL + (res << 6) + (res >> 2);
        return res;
    }
};

class Solution {
public:
    long long solveDP(const vector<int>& v1, int a, int t, int st, int s, long long p,
                      unordered_map<DPState, long long, DPStateHash>& mp) {
        if(a == v1.size()){
            if(!st) return 0;
            return (s > 0 && p % s == 0) ? 1LL : 0LL;
        }
        DPState state {a, t, st, s, p};
        if(mp.count(state))
            return mp[state];
        long long res = 0;
        int lim=0;
        if(t){
            lim=v1[a];
        }
        else lim=9;
        for(int d = 0; d <= lim; d++){
            int nt = t;
            if(t && d < lim) nt = 0;
            int ns = s;
            long long np = p;
            int nst = st;
            if(!st && d == 0){
            } else {
                if(!st){
                    nst = 1;
                    ns = d;
                    np = d;
                } else {
                    ns += d;
                    np *= d;
                }
            }
            res += solveDP(v1, a + 1, nt, nst, ns, np, mp);
        }
        mp[state] = res;
        return res;
    }
    long long beautifulCount(int x) {
        if(x < 1) return 0;
        string str = to_string(x);
        vector<int> v1;
        for(char c : str)
            v1.push_back(c - '0');
        unordered_map<DPState, long long, DPStateHash> mp;
        return solveDP(v1, 0, 1, 0, 0, 1LL, mp);
    }
    int beautifulNumbers(int l, int r) {
        long long res = beautifulCount(r) - beautifulCount(l - 1);
        return (int)res;
    }
};

