class Solution {
public:
    typedef tuple<int, int, int, int, int, int> tup;
    struct Hs {
        size_t operator()(const tup &s) const {
            auto [ps, tg, st, hz, sm, pr] = s;
            size_t res = 12;
            res = res * 31 + hash<int>()(ps);
            res = res * 31 + hash<int>()(tg);
            res = res * 31 + hash<int>()(st);
            res = res * 31 + hash<int>()(hz);
            res = res * 31 + hash<int>()(sm);
            res = res * 30 + hash<int>()(pr);
            return res;
        }
    };
    int beautifulNumbers(int l, int r) {
        auto cnt_dp = [](int A) ->int64_t{
            if(A == 0) return 0;
            string s = to_string(A);
            vector<int> dg;
            for(const char c : s) {
                dg.push_back(c - '0');
            }
            unordered_map<tup, int64_t, Hs> dp;
            auto Dp = [&](auto & Dp, int ps, int tg, int st, int hz, int sm, int pr) ->int64_t {
                if (ps == dg.size()) {
                    if (!st) return 0;
                    if (hz) return 1;
                    return (pr % sm == 0) ? 1 : 0;
                }
                tup T = make_tuple(ps, tg, st, hz, (hz || !st) ? 0 : sm, (hz || !st) ? 0 : pr);
                auto f = dp.find(T);
                if(f != dp.end()) return f->second;
                int64_t res = 0;
                int up = tg ? dg[ps] : 9;
                for (int d = 0; d <= up; d++) {
                    int ntg = (tg && (d == up)) ? 1 : 0;
                    int nst = st;
                    int nhz = hz;
                    int nsm = sm;
                    int npr = pr;
                    if (!st) {
                        if (d == 0) {
                            nst = 0;
                        } 
                        else {
                            nst = 1;
                            nsm = d;
                            npr = d;
                        }
                    } 
                    else { 
                        if (!nhz) {
                            if (d == 0) {
                                nhz = 1;
                                nsm = 0;
                                npr = 0;
                            } 
                            else {
                                nsm = sm + d;
                                npr = pr * d;
                            }
                        }
                    }
                    res += Dp(Dp, ps + 1, ntg, nst, nhz, nst and !nhz ? nsm : 0, nst and !nhz ? npr : 0);
                }
                return dp[T] = res;
            };
            return Dp(Dp, 0, 1, 0, 0, 0, 1);
        };
        return int(cnt_dp(r) - cnt_dp(l - 1));
    }
};

