int dp[81][82][10][2][2];
int vis[81][82][10][2][2];
int ttt = 0;

class Solution {
public:


    string curr;
    int m;


    int func(int idx, int mod, int sum, bool led, bool sm) {
        mod %= m;
        if(sum < 0) return 0;
        if(idx == curr.size()) {
            return mod == 0 && sum == 0;
        }

        if(vis[mod][sum][idx][led][sm] == ttt) return dp[mod][sum][idx][led][sm];
        vis[mod][sum][idx][led][sm] = ttt;

        int to = sm?9:curr[idx] - '0';
        int ans = 0;
        for (int i = 0; i <= to; ++i) {
            if(led == 0 && i == 0) {
                ans += func(idx + 1, mod % m, sum - i , led, sm || (i < curr[idx] - '0'));
            }
            else {
                ans += func(idx + 1, (mod*1ll*i) % m, sum - i, led||(i!=0), sm || (i < curr[idx] - '0'));
            }
        }
        return dp[mod][sum][idx][led][sm] = ans;
    }
    int beautifulNumbers(int l, int r) {

        curr = to_string(r);

        int ans = 0;
        for (int i = 1; i <= 81; ++i) {
            m = i;
            ttt++;
            ans += func(0, 1, i, 0, 0);

        }

        curr = to_string(l - 1);
        for (int i = 1; i <= 81; ++i) {
            m = i;
            ttt++;
            ans -= func(0, 1, i, 0, 0);
        }
        return ans;
    }

};

