int rec(int i,int flag,int cur,int sod,int prod,string& s){
    if(i==0) return prod==0 and cur==sod;
    if(dp[i][flag][cur][sod][prod]!=-1) return dp[i][flag][cur][sod][prod];
    int end=9,ans=0,d;
    if(flag==1) {
        d=s[s.size()-i]-'0';
        end=d;
    }
    for(int j=(cur==0);j<=end;j++){
        int nflag=flag;
        if(flag and j<d) nflag=0;
        int nprod=prod;
        nprod=(prod*j)%sod;
        ans+=rec(i-1,nflag,cur+j,sod,nprod,s);
    }
    return dp[i][flag][cur][sod][prod]=ans;
}
class Solution {
public:
    int beautifulNumbers(int l, int r) {
        memset(dp,-1,sizeof(dp));
        string rs=to_string(r),ls=to_string(l-1);
        int rans=0,lans=0;
        for(int i=1;i<=81;i++) for(int j=1;j<=rs.size();j++) rans+=rec(j,j==rs.size(),0,i,1,rs);
        memset(dp,-1,sizeof(dp));
        for(int i=1;i<=81;i++) for(int j=1;j<=ls.size();j++) lans+=rec(j,j==ls.size(),0,i,1,ls);
        return rans-lans;
    }
};

