class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def count_up_to(n):
            if n == 0:
                return 0

            digits = str(n)
            length = len(digits)

            memo = {}

            def dp(pos, tight, digit_sum, digit_product, started):
                if pos == length:
                    if not started:
                        return 0
                    return 1 if digit_sum > 0 and digit_product % digit_sum == 0 else 0

                key = (pos, tight, digit_sum, digit_product, started)
                if key in memo:
                    return memo[key]

                result = 0
                limit = int(digits[pos]) if tight else 9

                for d in range(limit + 1 if tight else 10):
                    new_started = started or d > 0

                    if not new_started:
                        result += dp(pos + 1, tight and d == limit, 0, 1, False)
                        continue

                    new_sum = digit_sum + d
                    new_product = digit_product * d if d > 0 else 0 if started else 1

                    if d == 0 and started:
                        new_product = 0

                    new_tight = tight and d == limit

                    result += dp(pos + 1, new_tight, new_sum, new_product, new_started)

                memo[key] = result
                return result

            return dp(0, True, 0, 1, False)

        return count_up_to(r) - count_up_to(l - 1)

