class Solution {
public:
    long long dp(string &s, int pos, int t, bool started, bool zero, int sum, int prod, unordered_map<long long, long long>&memo)
{
    if(pos == s.size())
    {
        return started && (zero || (prod%sum==0));
    }

    long long key = (((((long long)pos<<2)|t)<<1|started)<<1|zero);
    key = (key<<7)|sum;
    key = (key<<32)|prod;

    if(memo.count(key))
    {
        return memo[key];
    }
    long long ans = 0;
    int limit = t ? s[pos]-'0' : 9;
    for(int d=0; d<=limit; d++)
        {
            int nt=t && (d==limit);
            if(!started)
            {
                if(d==0)
                {
                    ans += dp(s, pos+1, nt, false, false, 0, 1, memo);
                }
                else
                {
                    ans += dp(s, pos+1, nt, true, false, d, d, memo);
                }
            }
            else
            {
                if(d==0)
                {
                    ans += dp(s, pos+1, nt, true, true, 0, 0, memo);
                }
                else
                {
                    if(zero)
                    {
                        ans += dp(s, pos+1, nt, true, true, 0, 0, memo);
                    }
                    else
                    {
                        ans += dp(s, pos+1, nt, true, false, sum+d, prod*d, memo);
                    }
                }
            }
        }
    return memo[key] = ans;
}
long long solve(long long x)
{
    if(x <= 0)
    {
        return 0;
    }
    string s = to_string(x);
    unordered_map<long long, long long> memo;
    return dp(s, 0, 1, false, false, 0, 1, memo);
}
    int beautifulNumbers(int l, int r) {
        return solve(r)-solve(l-1);
    }
};

