struct tuple_hash {
    template <class T1, class T2, class T3, class T4>
    size_t operator()(const tuple<T1, T2, T3, T4> &t) const {
        auto h1 = hash<T1>{}(get<0>(t));
        auto h2 = hash<T2>{}(get<1>(t));
        auto h3 = hash<T3>{}(get<2>(t));
        auto h4 = hash<T4>{}(get<3>(t));
        return h1 ^ (h2 << 1) ^ (h3 << 2) ^ (h4 << 3);
    }
};

unordered_map<tuple<int, int, int, int>, int, tuple_hash> memo;

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return solve(r) - solve(l-1);
    }

    int solve(int n) {
        if (!n) return 0;
        int len = log10(n) + 1;
        vector<int> v(len);
        for (int x = n, i = len - 1; x; x /= 10, i--) {
            v[i] = x % 10;
        }
        memo.clear();
        // cout << endl;
        return dp(v, 0, 1, 0, 1);
    }

    int dp(const vector<int>& v, int pos, int is_mx, int s, int p) {
        if (pos == v.size()) {
            return s && p % s == 0;
        }

        tuple<int, int, int, int> key = make_tuple(pos, is_mx, s, p);
        auto it = memo.find(key);
        if (it != memo.end()) return it->second;

        int lim = is_mx ? v[pos] : 9;
        int res = 0;
        for (int d = 0; d <= lim; d++) {
            res += dp(v, pos + 1, is_mx && d == lim, s + d, (s + d) ? p * d : 1);
        }

        // cout << pos << ' ' << is_mx << ' ' << s << ' ' << p << " = " << res << endl;
        memo[key] = res;
        return res;
    }
};

