class Solution {
    public int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }

    public int countBeautiful(int x) {
        if (x < 0) return 0;
        char[] digits = getCharArray(x);
        HashMap<String, Integer> dp = new HashMap<>();
        return solve(0, 1, 0, 1, digits, dp);
    }

    public char[] getCharArray(int x) {
        return String.valueOf(x).toCharArray();
    }

    public int solve(int i, int tight, int sum, int prod, char[] digits, HashMap<String, Integer> dp) {
        if (i == digits.length) {
            if (sum > 0 && prod % sum == 0) {
                return 1;
            } else {
                return 0;
            }
        }

        String key = i + " - " + tight + " - " + sum + " - " + prod;
        if (dp.containsKey(key)) return dp.get(key);

        int limit;
        if (tight == 1) {
            limit = digits[i] - '0';
        } else {
            limit = 9;
        }

        int count = 0, j = 0;
        while (j <= limit) {
            int newTight;
            if (tight == 1 && j == limit) {
                newTight = 1;
            } else {
                newTight = 0;
            }

            int newSum = sum + j;
            int newProd;
            if (j == 0 && sum == 0) {
                newProd = 1;
            } else {
                newProd = prod * j;
            }

            count += solve(i + 1, newTight, newSum, newProd, digits, dp);
            j++;
        }

        dp.put(key, count);
        return count;
    }
}

