class Solution {
public:
    int beautifulNumbers(int l, int r) {
        int r_count = count(to_string(r), 0, true, 1, 0, false);
        _memo.clear();
        int l_count = (l > 1 ? count(to_string(l - 1), 0, true, 1, 0, false) : 0);
        return r_count - l_count;
    }
private:
    unordered_map<string, int> _memo;
    int count(string_view boundary, int idx, int is_boundary, int product, int sum, int has_started) {
        if (idx == boundary.size()) {
            return has_started && product % sum == 0;
        }
        auto key = to_string(sum) + "#" + to_string(product) + "#" + to_string(has_started) + "#" + to_string(idx) + "#" + to_string(is_boundary);
        if (_memo.contains(key)) {
            return _memo[key];
        }
        int max_digit = 9;
        if (is_boundary) {
            max_digit = boundary[idx] - '0';
        }
        int res{0};
        for (int digit = 0; digit <= max_digit; ++digit) {
            if (!has_started) {
                if (digit == 0)
                    res += count(boundary, idx + 1, is_boundary & (max_digit == digit), 1, 0, false);
                else
                    res += count(boundary, idx + 1, is_boundary & (max_digit == digit), digit, digit, true);
            }
            else {
                if (digit == 0)
                    res += count(boundary, idx + 1, is_boundary & (max_digit == digit), 0, sum, has_started);
                else
                    res += count(boundary, idx + 1, is_boundary & (max_digit == digit), product * digit, sum + digit, has_started);
            }
        }
        return _memo[key] = res;
    }
int find_sum(string_view nums) {
    int sum{0};
    for (auto num : nums) {
        sum += num - '0';   
    }
    return sum;
}
int find_product(string_view nums) {
    int product = 1;
    for (auto num : nums) {
        product *= num;
    }
    return product;
}
};

