class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        # Helper function to compute digit DP
        def count_beautiful_numbers(n):
            s = str(n)
            length = len(s)

            @lru_cache(None)
            def dp(pos, tight, prod, summ, leading_zero):
                if pos == length:
                    # If the product is divisible by the sum, it's a beautiful number
                    return 0 if summ == 0 or prod % summ != 0 else 1

                result = 0
                limit = int(s[pos]) if tight else 9
                for d in range(0, limit + 1):
                    new_prod = prod * d if not leading_zero or d != 0 else prod
                    new_summ = summ + d if not leading_zero or d != 0 else summ
                    new_tight = tight and (d == limit)
                    new_leading_zero = leading_zero and (d == 0)
                    result += dp(pos + 1, new_tight, new_prod, new_summ, new_leading_zero)

                return result

            return dp(0, True, 1, 0, True)

        # Count beautiful numbers up to 'r' and subtract those below 'l'
        return count_beautiful_numbers(r) - count_beautiful_numbers(l - 1)

