class Solution:
    def beautifulNumbers(self, mol: int, jul: int) -> int:
        def can(lol):
            tim = list(map(int, str(lol)))  

            @lru_cache(None)  # Memoization
            def dp(vhh, tight, fghj, vbnm, asdf):
                if vhh == len(tim):
                    return 1 if (fghj > 0 and vbnm % fghj == 0) else 0

                limit = tim[vhh] if tight else 9
                res = 0

                for digit in range(0, limit + 1):
                    new_fghj = fghj + digit
                    new_vbnm = vbnm * (digit if digit > 0 or fghj > 0 else 1)
                    res += dp(vhh + 1, tight and (digit == limit), new_fghj, new_vbnm, asdf and digit == 0)

                return res

            return dp(0, True, 0, 1, True)

        return can(jul) - can(mol - 1)

