class Solution {
    private String numStr;
    private Map<Long, Long> memo;

    private long rec(int pos, boolean tight, boolean started, boolean hasZero, int sum, int e2, int e3, int e5, int e7) {
        if (pos == numStr.length()) {
            if (!started) return 0;
            if (hasZero) return 1;
            int prod = 1;
            for (int i = 0; i < e2; i++) prod *= 2;
            for (int i = 0; i < e3; i++) prod *= 3;
            for (int i = 0; i < e5; i++) prod *= 5;
            for (int i = 0; i < e7; i++) prod *= 7;
            return (sum != 0 && prod % sum == 0) ? 1 : 0;
        }

        long key = pos | (tight ? 1L : 0) << 4 | (started ? 1L : 0) << 5 | (hasZero ? 1L : 0) << 6
                | (sum << 7) | ((long) e2 << 14) | ((long) e3 << 20) | ((long) e5 << 26) | ((long) e7 << 32);

        if (memo.containsKey(key)) return memo.get(key);

        long result = 0;
        int limit = tight ? numStr.charAt(pos) - '0' : 9;
        for (int digit = 0; digit <= limit; digit++) {
            boolean nextTight = tight && (digit == limit);
            if (!started) {
                if (digit == 0) {
                    result += rec(pos + 1, nextTight, false, false, 0, 0, 0, 0, 0);
                } else {
                    int newSum = digit;
                    int newE2 = 0, newE3 = 0, newE5 = 0, newE7 = 0;
                    int temp = digit;
                    while (temp % 2 == 0) { newE2++; temp /= 2; }
                    while (temp % 3 == 0) { newE3++; temp /= 3; }
                    while (temp % 5 == 0) { newE5++; temp /= 5; }
                    while (temp % 7 == 0) { newE7++; temp /= 7; }
                    result += rec(pos + 1, nextTight, true, false, newSum, newE2, newE3, newE5, newE7);
                }
            } else {
                if (hasZero) {
                    result += rec(pos + 1, nextTight, true, true, sum + digit, 0, 0, 0, 0);
                } else {
                    if (digit == 0) {
                        result += rec(pos + 1, nextTight, true, true, sum, 0, 0, 0, 0);
                    } else {
                        int newSum = sum + digit;
                        int newE2 = e2, newE3 = e3, newE5 = e5, newE7 = e7;
                        int temp = digit;
                        while (temp % 2 == 0) { newE2++; temp /= 2; }
                        while (temp % 3 == 0) { newE3++; temp /= 3; }
                        while (temp % 5 == 0) { newE5++; temp /= 5; }
                        while (temp % 7 == 0) { newE7++; temp /= 7; }
                        result += rec(pos + 1, nextTight, true, false, newSum, newE2, newE3, newE5, newE7);
                    }
                }
            }
        }
        memo.put(key, result);
        return result;
    }

    private long countBeautiful(long x) {
        if (x < 1) return 0;
        numStr = String.valueOf(x);
        memo = new HashMap<>();
        return rec(0, true, false, false, 0, 0, 0, 0, 0);
    }

    public int beautifulNumbers(int l, int r) {
        return (int) (countBeautiful(r) - countBeautiful(l - 1));
    }
}

