        l1_str = str(l-1)
        r_str = str(r)
        @cache
        def dp(cStr, idx = 0, strict=True, prod=-1,sum=0):
            if idx == len(cStr):
                if sum == 0: return 0
                return prod % sum == 0
            dig = int(cStr[idx]) 
            lim = dig if strict else 9
            def calcProd(num):
                nonlocal prod
                if prod == -1: 
                    if num == 0:
                        return -1
                    return num
                return prod*num
            res = 0
            for i in range(lim+1):
                nxtProd = calcProd(i)
                nxtSum = sum + i
                nxtStrict = strict & (i == lim)
                res += dp(cStr, idx + 1, nxtStrict, nxtProd, nxtSum)
            return res
        return dp(r_str) - dp(l1_str)

