class Solution {
    public int beautifulNumbers(int l, int r) {
        return beautifulNumbers(r) - beautifulNumbers(l - 1);
    }

    private int beautifulNumbers(int num) {
        if (num == 0) {
            return 0;
        }

        char[] cs = String.valueOf(num).toCharArray();
        Map<String, Integer>[][][] cache = new Map[2][cs.length][100];
        for (Map[][] mat : cache) {
            for (Map[] row : mat) {
                for (int i = 0; i < row.length; i++) {
                    row[i] = new HashMap<>();
                }
            }
        }
        return beautifulNumbers(cs, 0, false, 0, new int[] {0, 0, 0, 0, 0}, cache);
    }

    private int beautifulNumbers(char[] cs, int ind, boolean smaller, int sum, int[] prod, Map<String, Integer>[][][] cache) {
        if (ind >= cs.length) {
            if (sum == 0) {
                return 0;
            }
            if (prod[0] > 0) {
                return 1;
            }
            for (int i = 0; i < prod[1] && sum % 2 == 0; i++) {
                sum /= 2;
            }
            for (int i = 0; i < prod[2] && sum % 3 == 0; i++) {
                sum /= 3;
            }
            for (int i = 0; i < prod[3] && sum % 5 == 0; i++) {
                sum /= 5;
            }
            for (int i = 0; i < prod[4] && sum % 7 == 0; i++) {
                sum /= 7;
            }
            return sum == 1 ? 1 : 0;
        }

        String key = Arrays.toString(prod);
        if (cache[smaller ? 1 : 0][ind][sum].containsKey(key)) {
            return cache[smaller ? 1 : 0][ind][sum].get(key);
        }

        int count = 0;
        for (int i = 0; i <= (smaller ? 9 : cs[ind] - '0'); i++) {
            switch (i) {
                case 0:
                    if (sum != 0) {
                        prod[0]++;
                    }
                    break;
                case 1:
                    break;
                case 2:
                    prod[1]++;
                    break;
                case 3:
                    prod[2]++;
                    break;
                case 4:
                    prod[1] += 2;
                    break;
                case 5:
                    prod[3]++;
                    break;
                case 6:
                    prod[1]++;
                    prod[2]++;
                    break;
                case 7:
                    prod[4]++;
                    break;
                case 8:
                    prod[1] += 3;
                    break;
                case 9:
                    prod[2] += 2;
                    break;
            }
            count += beautifulNumbers(cs, ind + 1, smaller || i < cs[ind] - '0', sum + i, prod, cache);
            switch (i) {
                case 0:
                    if (sum != 0) {
                        prod[0]--;
                    }
                    break;
                case 1:
                    break;
                case 2:
                    prod[1]--;
                    break;
                case 3:
                    prod[2]--;
                    break;
                case 4:
                    prod[1] -= 2;
                    break;
                case 5:
                    prod[3]--;
                    break;
                case 6:
                    prod[1]--;
                    prod[2]--;
                    break;
                case 7:
                    prod[4]--;
                    break;
                case 8:
                    prod[1] -= 3;
                    break;
                case 9:
                    prod[2] -= 2;
                    break;
            }
        }

        cache[smaller ? 1 : 0][ind][sum].put(key, count);
        return count;
    }
}

