                print(num)


    def beautifulNumbers(self, l: int, r: int) -> int:

        #self.printout(583, 623)
        #return 0
        digs = []
        temp = r
        while temp > 0:
            digs.append(temp%10)
            temp //= 10
        self.cache = {}

        options = self.get_combs(len(digs))
        usable = []
        for option in options:
            if self.works(option):
                usable.append(option)
        self.usable = usable
        self.facts = [1, 1]
        for i in range(2, 11):
            self.facts.append(i * self.facts[i-1])


        #print(self.mhelper([0, 0, 0, 0, 0, 2, 0, 0, 0, 0], 2))
        #print(self.multiplic([4, 6, 6]))



        #print(self.facts)
        return self.helper(r) - self.helper(l - 1)

    def helper(self, num):
        digs = []
        temp = num
        while temp > 0:
            digs.append(temp%10)
            temp //= 10
        digs = digs[::-1]
        #print(digs)
        result = 0
        for option in self.usable:
            if len(option) < len(digs):
                result += self.multiplic(option)
                continue
                #print(option)
            if len(option) > len(digs):
                continue
            counts = [0] * 10
            for num in option:
                counts[num] += 1
            #print("{}, {} => {}".format(digs, option, self.same_helper(digs, counts, True)))
            result += self.same_helper(digs, counts, True)
        return result

    def same_helper(self, digs, counts, is_first=False):
        if len(digs) == 0:
            return 1
        max_first = digs[0]
        result = 0
        for dig in range(1 if is_first else 0, max_first):
            if counts[dig] > 0:
                counts[dig] -= 1
                result += self.mhelper(counts, len(digs)-1)
                counts[dig] += 1
        if counts[max_first] > 0:
            counts[max_first] -= 1
            result += self.same_helper(digs[1:], counts, False)
            counts[max_first] += 1
        return result

    def multiplic(self, option):
        counts = [0] * 10
        for num in option:
            counts[num] += 1
        result = 0
        for num in range(1, 10):
            if counts[num] == 0:
                continue
            counts[num] -= 1
            result += self.mhelper(counts, len(option)-1)
            counts[num] += 1
        return result

    def mhelper(self, counts, sublen):
        res = self.facts[sublen]
        for num in range(0, 10):
            if counts[num] != 0:
                res //= self.facts[counts[num]]
        return res

    def works(self, option):
        total = 0
        prod = 1
        for num in option:
            total += num
            prod *= num

        return total != 0 and prod % total == 0

    @functools.cache
    def get_combs(self, maxlen):
        result = []
        for sublen in range(1, maxlen+1):
            result += self.get_combs_by_len(sublen, 0)
        return result

    def get_combs_by_len(self, sublen, start_digit):
        #if (sublen, start_digit) in self.cache:
        #    return self.cache[(sublen, start_digit)]
        if start_digit == 9:
            return [[9] * sublen]
        if sublen == 0:
            return [[]]
        result = self.get_combs_by_len(sublen, start_digit+1)
        for subres in self.get_combs_by_len(sublen-1, start_digit):
            result.append([start_digit] + subres)
        #if sublen <= 3:
        #    self.cache[(sublen, start_digit)] = result
        return result

