class Solution {
vector<int> tmp;
public:
    vector<int> getDigits(int num) {
        vector<int> digits;
        while (num > 0) {
            digits.push_back(num % 10);
            num /= 10;
        }
        reverse(digits.begin(), digits.end());
        return digits;
    }
    int func(int idx, int sum, int prodModSum, bool tight, const vector<int>& digits,int dsum) {
        if (idx == digits.size()) {
            if (sum == dsum && prodModSum == 0){
                // cout<<"tmp"<<": ";
                // for(auto i:tmp) cout<<i<<" ";
                // cout<<endl;
                return 1;
            }
            return 0;
        }

        if (dp[sum][prodModSum][tight][idx] != -1) {
            return dp[sum][prodModSum][tight][idx];
        }

        int limit = tight ? digits[idx] : 9;
        int result = 0;

        for (int digit=0;digit<=limit;digit++) {
            if (sum + digit > dsum) continue;
            int prod = (prodModSum * digit) % (dsum);
            if(digit==0 && sum==0){
                prod=1;
            }
            tmp.push_back(digit);
            result += func(idx + 1, sum + digit, prod, tight && (digit == limit), digits,dsum);
            tmp.pop_back();
        }

        dp[sum][prodModSum][tight][idx] = result;
        return result;
    }
    int cnt(int x) {
        if(x==0) return 0;
        vector<int> digits = getDigits(x);
        // for(auto i:digits){
        //     cout<<i<<",";
        // }
        // cout<<endl;
        int ans=0;
        for(int dsum=1;dsum<=9*digits.size();dsum++){
            memset(dp, -1, sizeof(dp));  
            ans+=func(0, 0, 1, true, digits,dsum);  
        }
        return ans;
    }
    int beautifulNumbers(int l, int r) {
        int rsh=cnt(r);
        int lsh=cnt(l-1);
        // cout<<rsh<<" "<<lsh<<endl;
        return rsh-lsh;
    }
