class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def calc(x):
            if x <= 10:
                return x
            sx = str(x)
            def good(p2, p3, p5, p7, sum):
                if sum == 1:
                    return True
                np = [p2, p3, p5, p7]
                p = [2,3,5,7]
                n = [0,0,0,0]
                for i in range(len(p)):
                    a = p[i]
                    while sum % a == 0:
                        sum //= a
                        n[i] += 1
                        if n[i] > np[i]:
                            return False
                if sum > 1:
                    return False
                return True
            def count(x, p):
                if x == 0:
                    return 10
                ret = 0
                while x > 1 and x % p == 0:
                    ret += 1
                    x = x // p
                return ret
            @cache
            def dfs(idx: int, start: bool, end: bool, zero: bool, p2:int, p3:int, p5:int, p7:int, sum:int)->int:
                #print(idx, start, end, zero, p2, p3, p5, p7, sum)
                if idx == len(sx):
                    #print(p2, p3, p5, p7, sum,  good(p2, p3, p5, p7, sum))
                    return 1 if (start and (zero or good(p2, p3, p5, p7, sum))) else 0
                ret = 0
                if not start:
                    ret = dfs(idx + 1, start, False, zero, p2, p3, p5, p7, sum)
                l, r = 0, 9
                if not start:
                    l = 1
                if end:
                    r = min(r, ord(sx[idx]) - ord('0'))
                for i in range(l, r + 1):
                    ret += dfs(idx + 1, True, end and (i == r), zero or (i == 0),
                               min(7, p2 + count(i, 2)), min(4, p3 + count(i, 3)),
                               min(2, p5 + count(i, 5)), min(2, p7 + count(i, 7)), sum + i)
                return ret
            return dfs(0, False, True, False, 0, 0, 0, 0, 0)
        return calc(r) - calc(l - 1)

