class Solution {
public:
    unordered_map<string, int> dp;

    int Helper(string& x, int i, int s, int p, bool tgt, bool ld) {
        if (i == x.size()) {
            if (s == 0 || (p % s == 0)) return 1;
            return 0;
        }

        string key = to_string(i) + "|" + to_string(s) + "|" + to_string(p) + "|" + to_string(tgt) + "|" + to_string(ld);
        if (dp.find(key) != dp.end()) return dp[key];

        int cnt = 0;
        int lim = tgt ? x[i] - '0' : 9;

        for (int j = 0; j <= lim; j++) {
            int sum = (ld && j == 0) ? 0 : (s + j);
            int prod = (ld && j == 0) ? 1 : (p * j);
            bool ntgt = tgt && (j == lim);
            bool nld = ld && (j == 0);
            cnt += Helper(x, i + 1, sum, prod, ntgt, nld);
        }

        return dp[key] = cnt;
    }

    int beautifulNumbers(int l, int r) {
        string rg = to_string(r);
        l--;
        string lf = to_string(l);
        dp.clear();
        int right = Helper(rg, 0, 0, 1, true, true);
        dp.clear();
        int left = Helper(lf, 0, 0, 1, true, true);
        return right - left;
    }
};

