        length = len(digits)

        @lru_cache(None)
        def dp(pos, tight, sum_digits, prod_digits, started):
            if pos == length:
                return 1 if started and prod_digits % sum_digits == 0 else 0

            limit = digits[pos] if tight else 9
            result = 0

            for digit in range(0, limit + 1):
                new_tight = tight and (digit == limit)
                new_sum = sum_digits + digit
                new_prod = prod_digits * digit if digit != 0 or started else 1
                new_started = started or digit > 0
                result += dp(pos + 1, new_tight, new_sum, new_prod, new_started)

            return result

        return dp(0, True, 0, 1, False)

    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.helper(r) - self.helper(l - 1)

