class Solution {
  public int beautifulNumbers(int l, int r) {
    return helper(r) - helper(l - 1);
  }

  private int countValid(Map<String, Integer> dp, List<Integer> list, int idx, int prod, int sum, boolean b, boolean s) {
    if (idx >= list.size()) {
      if (!s) {
        return 0;
      }
      return (sum != 0 && prod % sum == 0) ? 1 : 0;
    }

    String key = idx + "," + prod + "," + sum + "," + b;
    if (dp.containsKey(key)) {
      return dp.get(key);
    }
    int temp = b ? list.get(idx) : 9;
    int ret = 0;
    for (int i = 0; i <= temp; i++) {
      boolean nS = s || (i != 0);
      int newProd = prod, newSum = sum;
      if (nS) {
        if (!s && i != 0) {
          newProd = i;
        }
        else if (s) {
          newProd = prod * i;
        }
        newSum = sum + i;
      }

      boolean newB = b && (i == temp);
      ret += countValid(dp, list, idx + 1, newProd, newSum, newB, nS);
    }

    dp.put(key, ret);
    return ret;
  }

  private int helper(int n) {
    if (n == 0) {
      return 0;
    }
    List<Integer> list = new ArrayList<>();
    while (n > 0) {
      list.add(n % 10);
      n /= 10;
    }
    Collections.reverse(list);
    Map<String, Integer> dp = new HashMap<>();
    return countValid(dp, list, 0, 1, 0, true, false);
  }
}

