class Solution {
public:
    unordered_map<string, int> dp;

// Recursive function for Digit DP
int countBeautifulHelper(vector<int> &digits, int pos, int sum, int product, bool tight, bool leadingZero) {
    if (pos == digits.size()) 
        return (sum > 0 && product % sum == 0);  // A number is beautiful if product % sum == 0

    string key = to_string(pos) + "-" + to_string(sum) + "-" + to_string(product) + "-" + to_string(tight) + "-" + to_string(leadingZero);
    if (dp.count(key)) return dp[key];

    int limit = tight ? digits[pos] : 9;
    int count = 0;

    for (int d = 0; d <= limit; d++) {
        bool newLeadingZero = leadingZero && (d == 0);
        count += countBeautifulHelper(digits, pos + 1, sum + d, (newLeadingZero ? 1 : product * d), tight && (d == limit), newLeadingZero);
    }

    return dp[key] = count;
}

// Helper function to extract digits and call recursive function
int countBeautiful(int num) {
    if (num == 0) return 0;  

    vector<int> digits;
    while (num) {
        digits.push_back(num % 10);
        num /= 10;
    }
    reverse(digits.begin(), digits.end());  

    dp.clear();
    return countBeautifulHelper(digits, 0, 0, 1, true, true);
}
    int beautifulNumbers(int l, int r) {
         return countBeautiful(r) - countBeautiful(l - 1);
    }
};

