class Solution:

    def count_lesser_equals(self, already_lesser, d_prod, d_sum, idx, is_first):
        if idx == len(self.target):
            if d_sum == 0 or d_prod%d_sum != 0:
                return 0
            else:
                return 1
        hash_key = (already_lesser, d_prod, d_sum, idx, is_first)
        if hash_key in self.dp:
            return self.dp[hash_key]

        cnt = 0
        for d in range(10):
            if is_first and d == 0:
                continue
            if d > self.target[idx]:
                if not already_lesser:
                    break
                else:
                    cnt += self.count_lesser_equals(True, d_prod*d, d_sum+d, idx+1, False)
            elif d == self.target[idx]:
                cnt += self.count_lesser_equals(already_lesser, d_prod*d, d_sum+d, idx+1, False)
            else:
                cnt += self.count_lesser_equals(True, d_prod*d, d_sum+d, idx+1, False)
        self.dp[hash_key] = cnt
        return cnt

    def count_beautiful_lesser_equals(self, num):
        if num == 0:
            return 0

        self.target = []
        while num > 0:
            self.target.append(num%10)
            num //= 10
        self.target = list(reversed(self.target))

        self.dp = {}
        cnt = 0
        for idx in range(1, len(self.target)):
            cnt += self.count_lesser_equals(True, 1, 0, idx, True)

        cnt += self.count_lesser_equals(False, 1, 0, 0, True)
        return cnt



    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.count_beautiful_lesser_equals(r)-self.count_beautiful_lesser_equals(l-1)

