#include <string>
#include <unordered_map>
#include <tuple>
#include <vector>
using namespace std;

typedef long long ll;


struct State {
    int pos, tight, started, hasZero, sum, prod;
    bool operator==(const State &other) const {
        return pos==other.pos && tight==other.tight && started==other.started &&
               hasZero==other.hasZero && sum==other.sum && prod==other.prod;
    }
};

struct HASH_state {
    size_t operator()(const State &s) const {
        size_t h1 = std::hash<int>()(s.pos);
        size_t h2 = std::hash<int>()(s.tight);
        size_t h3 = std::hash<int>()(s.started);
        size_t h4 = std::hash<int>()(s.hasZero);
        size_t h5 = std::hash<int>()(s.sum);
        size_t h6 = std::hash<int>()(s.prod);

        return (((((h1 * 31 + h2) * 31 + h3) * 31 + h4) * 31 + h5) * 31 + h6);
    }
};

class Solution {
public:

    ll f(const string &s, int pos, int tight, int started, int hasZero, int sum, int prod) {
        if (pos == s.size()) {

            if (!started) return 0;

            if (hasZero) return 1;

            return (prod % sum == 0) ? 1 : 0;
        }

        State st = {pos, tight, started, hasZero, sum, prod};
        if(map_memo.count(st)) return map_memo[st];

        int limit = (tight ? (s[pos]-'0') : 9);
        ll ans = 0;

        for (int dig = 0; dig <= limit; dig++) {
            int n_is_tight = tight && (dig == limit);
            int n_is_started = started;
            int Zero_in_n = hasZero;
            int nsum = sum;
            int nprod = prod;

            if (!started && dig == 0) {

                ans += f(s, pos+1, n_is_tight, 0, 0, 0, 1);
            } else {

                n_is_started = 1;
                if (dig == 0) {

                    Zero_in_n = 1;

                    nprod = 0;
                    nsum += 0; 
                } else {

                    if (!Zero_in_n) {
                        nsum += dig;
                        nprod *= dig;
                    }
                }
                ans += f(s, pos+1, n_is_tight, n_is_started, Zero_in_n, nsum, nprod);
            }
        }

        map_memo[st] = ans;
        return ans;
    }


    ll countUpTo(int num) {
        string s = to_string(num);
        map_memo.clear();
        return f(s, 0, 1, 0, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {

        return (int)(countUpTo(r) - countUpTo(l-1));
    }

private:
    unordered_map<State, ll, HASH_state> map_memo;
};

