using System;
using System.Collections.Generic;

public class Solution {
    private string numberString;
    private Dictionary<string, int> memoization;

    public int BeautifulNumbers(int left, int right) {
        return CountValidNumbers(right) - CountValidNumbers(left - 1);
    }

    private int CountValidNumbers(int num) {
        if (num < 1) return 0;
        numberString = num.ToString();
        memoization = new Dictionary<string, int>();
        return DepthFirstSearch(0, true, false, false, 0, 0, 0, 0, 0);
    }

    private int DepthFirstSearch(int index, bool isLimited, bool hasStarted, bool containsZero, int sumDigits, int powerTwo, int powerThree, int powerFive, int powerSeven) {
        if (index == numberString.Length) {
            if (!hasStarted) return 0;
            if (containsZero) return 1;
            int product = (int)(Math.Pow(2, powerTwo) * Math.Pow(3, powerThree) * Math.Pow(5, powerFive) * Math.Pow(7, powerSeven));
            return (sumDigits != 0 && product % sumDigits == 0) ? 1 : 0;
        }

        string key = $"{index},{isLimited},{hasStarted},{containsZero},{sumDigits},{powerTwo},{powerThree},{powerFive},{powerSeven}";
        if (memoization.ContainsKey(key)) return memoization[key];

        int count = 0;
        int maxDigit = isLimited ? numberString[index] - '0' : 9;

        for (int digit = 0; digit <= maxDigit; digit++) {
            bool newLimit = isLimited && (digit == maxDigit);
            if (!hasStarted) {
                if (digit == 0) {
                    count += DepthFirstSearch(index + 1, newLimit, false, false, 0, 0, 0, 0, 0);
                } else {
                    var factorPowers = GetFactorPowers(digit);
                    count += DepthFirstSearch(index + 1, newLimit, true, false, digit, factorPowers[0], factorPowers[1], factorPowers[2], factorPowers[3]);
                }
            } else {
                int newSum = sumDigits + digit;
                if (containsZero || digit == 0) {
                    count += DepthFirstSearch(index + 1, newLimit, true, true, newSum, 0, 0, 0, 0);
                } else {
                    var factorPowers = GetFactorPowers(digit);
                    count += DepthFirstSearch(index + 1, newLimit, true, false, newSum, powerTwo + factorPowers[0], powerThree + factorPowers[1], powerFive + factorPowers[2], powerSeven + factorPowers[3]);
                }
            }
        }

        memoization[key] = count;
        return count;
    }

    private int[] GetFactorPowers(int num) {
        int[] powerFactors = new int[4];
        while (num % 2 == 0) { powerFactors[0]++; num /= 2; }
        while (num % 3 == 0) { powerFactors[1]++; num /= 3; }
        while (num % 5 == 0) { powerFactors[2]++; num /= 5; }
        while (num % 7 == 0) { powerFactors[3]++; num /= 7; }
        return powerFactors;
    }
}

