class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return countUpTo(r) - countUpTo(l - 1);
    }
private:

    int dp(int pos,
                 bool tight,
                 bool leadingZero,
                 int sum,
                 int prod,
                 const vector<int>& digits,
                 unordered_map<string,int>& memo)
    {

        if (pos == (int)digits.size()) {
            if (sum > 0 && (prod % sum == 0))
                return 1;
            return 0;
        }

        ostringstream oss;
        oss << pos << "," << (tight ? 1 : 0) << "," << (leadingZero ? 1 : 0)
            << "," << sum << "," << prod;
        string key = oss.str();

        if (memo.find(key) != memo.end()) {
            return memo[key];
        }

        int ways = 0;
        int limit = tight ? digits[pos] : 9;

        for (int dig = 0; dig <= limit; dig++) {
            bool nextTight = (tight && (dig == limit));
            bool nextLeadingZero = (leadingZero && dig == 0);

            int nextSum  = nextLeadingZero ? 0 : (sum + dig);
            int nextProd = nextLeadingZero ? 1 : (prod * dig);

            ways += dp(pos + 1, nextTight, nextLeadingZero, nextSum, nextProd,
                       digits, memo);
        }

        memo[key] = ways;
        return ways;
    }

    int countUpTo(int n) {
        if (n < 0) 
            return 0;
        vector<int> digits;
        {
            string s = to_string(n);
            for (char c: s) {
                digits.push_back(c - '0');
            }
        }    
        unordered_map<string,int> memo;
        return dp(0, true, true, 0LL, 1LL, digits, memo);
    }
