string target;
int dp[2][10][82][82];
int D;

int recur(int pos, bool sm=0, bool le=1, int dsum=0, int prod=1){
    if(pos==-1) return !le and dsum==D and !prod;
    int& res = dp[le][pos][dsum][prod];
    if(sm && res != -1) return res;
    int lim = sm?9:target[pos]-'0', ans = 0;
    for(int d = 0; d <= lim; d++)
        ans+=recur(pos-1,sm or d<lim, le?!d:0, dsum+d, (le?!d:0)?prod:(prod*d)%D);
    return sm ? res = ans : ans;
}

class Solution {
public:
    int beautifulNumbers(int l, int r) {

        int ans = 0;
        for(D = 1; D < 82; D++){
            memset(dp, -1, sizeof(dp));
            target = to_string(r); reverse(all(target));
            int tot = recur((int)target.size()-1);
            int len = sz(target);
            target = to_string(l-1); 
            //while(sz(target) < len) target='0'+target;
            reverse(all(target));
            tot-=recur((int)target.size()-1);
            ans+=tot;
        }
        return ans;
    }
};

