class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.count_beautiful(r) - self.count_beautiful(l - 1)

    def count_beautiful(self, n: int) -> int:
        if n < 1:
            return 0
        digits = list(str(n))
        memo = {}
        return self.find_beautiful(0, True, False, 0, 1, digits, memo)

    def find_beautiful(self, pos: int, is_tight: bool, has_started: bool, digit_sum: int, digit_product: int, digits: list, memo: dict) -> int:
        if pos == len(digits):
            return 1 if has_started and digit_sum > 0 and digit_product % digit_sum == 0 else 0

        key = (pos, is_tight, has_started, digit_sum, digit_product)
        if key in memo:
            return memo[key]

        limit = int(digits[pos]) if is_tight else 9
        count = 0

        for d in range(limit + 1):
            next_tight = is_tight and (d == limit)
            new_started = has_started or (d != 0)
            new_sum, new_prod = digit_sum, digit_product

            if new_started:
                new_sum = d if not has_started else digit_sum + d
                new_prod = d if not has_started else digit_product * d

            count += self.find_beautiful(pos + 1, next_tight, new_started, new_sum, new_prod, digits, memo)

        memo[key] = count
        return count

