struct TupleHash
{
    template <typename T>
    static void hsh(size_t &seed, const T &val)
    {
        seed ^= hash<T>()(val) + 0x9e3779b9 + (seed << 6) + (seed >> 2);
    }

    size_t operator()(const tuple<int, bool, bool, int, ll> &key) const
    {
        size_t seed = 0;
        hsh(seed, get<0>(key));
        hsh(seed, get<1>(key));
        hsh(seed, get<2>(key));
        hsh(seed, get<3>(key));
        hsh(seed, get<4>(key));
        return seed;
    }
};

class Solution
{
public:
    int beautifulNumbers(int l, int r)
    {
        return func1(r) - func1(l - 1);
    }

private:
    unordered_map<tuple<int, bool, bool, int, ll>, ll, TupleHash> memo;
    ll func1(int n)
    {
        if (n < 1)
            return 0;
        string digs = to_string(n);
        memo.clear();
        return func2(0, true, false, 0, 1, digs);
    }
    ll func2(int pos, bool tight, bool started, int sum, ll prod, const string &digs)
    {
        if (pos == digs.size())
        {
            return (started && sum > 0 && prod % sum == 0) ? 1 : 0;
        }

        tuple<int, bool, bool, int, ll> key = {pos, tight, started, sum, prod};
        if (memo.count(key))
            return memo[key];

        int till = tight ? digs[pos] - '0' : 9;
        ll total = 0;

        for (int d = 0; d <= till; d++)
        {
            bool nxt = tight && (d == till);
            bool nxtst = started || (d != 0);
            int nxtsm = sum;
            ll nxtprd = prod;

            if (nxtst)
            {
                if (!started)
                {
                    nxtsm = d;
                    nxtprd = d;
                }
                else
                {
                    nxtsm += d;
                    nxtprd *= d;
                }
            }
            total += func2(pos + 1, nxt, nxtst, nxtsm, nxtprd, digs);
        }

        return memo[key] = total;
    }
};

