class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_up_to(n: int) -> int:
            s = str(n)
            N = len(s)
            from functools import lru_cache
            @lru_cache(maxsize=None)
            def dfs(index, is_tight, is_leading, digit_sum, digit_product):
                if index == N:
                    if is_leading or digit_sum == 0:
                        return 0
                    return 1 if digit_product % digit_sum == 0 else 0
                total = 0
                max_digit = int(s[index]) if is_tight else 9
                for d in range(max_digit + 1):
                    new_tight = is_tight and (d == max_digit)
                    if is_leading and d == 0:
                        total += dfs(index + 1, new_tight, True, digit_sum, digit_product)
                    else:
                        total += dfs(index + 1, new_tight, False, digit_sum + d, digit_product * d)
                return total
            return dfs(0, True, True, 0, 1)
        return count_up_to(r) - count_up_to(l - 1)

