class Solution {
public:
    int beautifulNumbers(int l, int r) {
        auto cnt = [&](int x) -> long long {
            if (x < 0) 
                return 0;
            string str = to_string(x);
            int n = str.size();
            unordered_map<string, long long> mem;
            auto dfs = [&](auto& self, int p, int t, int st, int sm, int pr) -> long long
            {
                if (p == n) 
                {
                    if (!st) 
                        return 0;
                    if (pr % sm == 0) 
                        return 1LL;
                    else 
                        return 0LL;
                }
                string key = to_string(p) + "_" + to_string(t) + "_" + to_string(st) + "_" + to_string(sm) + "_" + to_string(pr);
                if (mem.count(key)) 
                    return mem[key];
                long long w = 0;
                int lim = (t ? str[p] - '0' : 9);
                for (int d = 0; d <= lim; d++) {
                    int nt = (t && d == lim);
                    int nst = st, nsm = sm, npr = pr;
                    if (!st && d == 0) ;
                    else if (!st && d > 0) 
                    {
                        nst = 1;
                        nsm = d;
                        npr = d;
                    } 
                    else 
                    {
                        nsm += d;
                        if (d == 0) 
                            npr = 0;
                        else 
                            npr *= d;
                    }

                    w += self(self, p + 1, nt, nst, nsm, npr);
                }
                mem[key] = w;
                return w;
            };

            return dfs(dfs, 0, 1, 0, 0, 1);
        };

        int ans = (int)(cnt(r) - cnt(l - 1));
        return ans;
    }
};

