class Solution {
    char[] s;
    Map<String, Integer> memo = new HashMap<>();
    public int beautifulNumbers(int l, int r) {
        return count(r) - count(l - 1);
    }

    private int count(int x) {
        if(x < 0) return 0;
        s = Integer.toString(x).toCharArray();
        memo.clear();
        return f(0, 0, 1, true, false);
    }
    private int f(int i, int sum, int prod, boolean isLimit, boolean isNum) {
        if(i == s.length)
            return isNum ? (prod % sum == 0 ? 1 : 0) : 0;
        String key = " " + i + (isLimit ? 1 : 0) + (isNum ? 1 : 0) + sum + prod;
        if(!isLimit && isNum && memo.containsKey(key))
            return memo.get(key);
        int res = 0;
        if(!isNum)
            res += f(i + 1, sum, prod, false, false);
        int up = isLimit ? s[i] - '0' : 9;
        for (int d = isNum ? 0 : 1; d <= up; d++) {
            boolean ntight = isLimit && (d == up);
            int nsum = isNum ? sum + d : d;
            int nprod = isNum ? prod * d : d;
            res += f(i + 1, nsum, nprod, ntight, true);
        }
        if(!isLimit && isNum)
            memo.put(key, res);
        return res;
    }
}

