#include <string>
#include <unordered_map>
using namespace std;
using ll = long long;

class Solution {
public:
    // Global variables used by the DP functions.
    string s;  // the digit representation of the current X
    unordered_map<string, ll> memo_nozero;       // memo for counting numbers with no zero digits
    unordered_map<string, ll> memo_beautiful;      // memo for counting nozero numbers that are "beautiful"

    // DP to count numbers (in [1, X]) that have NO zero digit.
    // Parameters:
    //   pos: current index in the string s
    //   tight: whether we are bounded by the prefix of s
    //   started: whether we have begun placing a nonleading digit
    ll dp_nozero(int pos, bool tight, bool started) {
        if (pos == s.size()) {
            // We count only positive numbers (i.e. that have "started")
            return (started ? 1LL : 0LL);
        }
        string key = to_string(pos) + "_" + to_string(tight) + "_" + to_string(started);
        if (memo_nozero.find(key) != memo_nozero.end()) return memo_nozero[key];
        int limit = tight ? (s[pos] - '0') : 9;
        ll res = 0;
        // Option 1: if we haven't started, we can skip this digit by placing a 0.
        // (In our DP, choosing 0 when not started does not start the number.)
        if (!started) {
            // Even if 0 is within the limit, this does NOT count as placing a digit (so no zero digit is introduced)
            bool nTight = tight && (0 == limit);
            res += dp_nozero(pos + 1, nTight, false);
        }
        // Option 2: Place a nonzero digit (only allowed if the digit is >=1).
        for (int dig = 1; dig <= limit; dig++) {
            // Once we place a nonzero digit, we have "started" the number.
            bool nTight = tight && (dig == limit);
            res += dp_nozero(pos + 1, nTight, true);
        }
        memo_nozero[key] = res;
        return res;
    }

    // DP to count numbers with NO zero digit that are "beautiful"
    // Parameters:
    //   pos: current digit index
    //   tight: whether we are restricted by the prefix of s
    //   started: whether we have begun the number (i.e. placed a nonzero digit)
    //   sum: the current digit-sum
    //   prod: the current digit-product
    ll dp_beautiful(int pos, bool tight, bool started, int sum, ll prod) {
        if (pos == s.size()) {
            // If the number has started, check if product is divisible by sum.
            if (started && sum != 0)
                return (prod % sum == 0 ? 1LL : 0LL);
            return 0LL;
        }
        // Build a key from the state.
        string key = to_string(pos) + "_" + to_string(tight) + "_" + to_string(started) 
                     + "_" + to_string(sum) + "_" + to_string(prod);
        if (memo_beautiful.find(key) != memo_beautiful.end())
            return memo_beautiful[key];
        int limit = tight ? (s[pos] - '0') : 9;
        ll res = 0;
        if (!started) {
            // We have not started yet.
            // Option: skip this digit (i.e. choose 0) to remain not started.
            if (0 <= limit) {
                bool nTight = tight && (0 == limit);
                res += dp_beautiful(pos + 1, nTight, false, 0, 1);
            }
            // Option: start the number by choosing a nonzero digit.
            for (int dig = 1; dig <= limit; dig++) {
                bool nTight = tight && (dig == limit);
                res += dp_beautiful(pos + 1, nTight, true, dig, dig);
            }
        } else {
            // The number has already started.
            // Now we are only allowed to choose digits 1..limit (we cannot use 0 because that would violate the "no zero digit" condition).
            for (int dig = 1; dig <= limit; dig++) {
                bool nTight = tight && (dig == limit);
                res += dp_beautiful(pos + 1, nTight, true, sum + dig, prod * dig);
            }
        }
        memo_beautiful[key] = res;
        return res;
    }

    // Count beautiful numbers in the range [1, X]
    // A number is beautiful if either:
    //   (a) It has at least one zero digit (automatically beautiful), OR
    //   (b) It has no zero digit and its digit-product is divisible by its digit-sum.
    ll count_beautiful(int X) {
        if (X <= 0) return 0LL;
        s = to_string(X);
        memo_nozero.clear();
        memo_beautiful.clear();
        // Count numbers with no zero digit.
        ll cnt_nozero = dp_nozero(0, true, false);
        // Count among these (nozero numbers) those that are beautiful.
        ll cnt_beautiful_nozero = dp_beautiful(0, true, false, 0, 1);
        // The total positive numbers up to X is X.
        // Hence the numbers that have at least one zero digit are:
        //      X - (numbers with no zero digit)
        ll cnt_with_zero = X - cnt_nozero;
        return cnt_with_zero + cnt_beautiful_nozero;
    }

    // Main function: returns the count of beautiful numbers in [l, r]
    int beautifulNumbers(int l, int r) {
        // Use our helper to count for [1, r] and subtract count for [1, l-1]
        return (int)(count_beautiful(r) - count_beautiful(l - 1));
    }
};

