class Solution {
public:
    typedef long long ll;
    ll helper(int i, bool k, bool j, ll s, ll p, const string& temp,
        vector<vector<vector<unordered_map<ll, ll>>>>& dp) {
        if (i == temp.size()) {
            return !j ? 0 : (s && p % s == 0) ? 1 : 0;
        }

        int t = k ? 1 : 0, st = j ? 1 : 0;
        ll hash = (p & (ll)(pow(2, 32) - 1)) | ((ll)s << 32);
        if (dp[i][t][st].find(hash) != dp[i][t][st].end()) {
            return dp[i][t][st][hash];
        }

        ll ans = 0;
        int mx = k ? temp[i] - '0' : 9;
        for (ll d = 0, ns = s, np = p; d <= mx; d++, ns = s, np = p) {
            if (j || (d != 0)) {
                if (j) {
                    np *= d;
                    ns += d;
                }
                else {
                    np = d;
                    ns = d;
                }
            }
            ans += helper(i + 1, k && (d == mx), j || (d != 0), ns, np, temp, dp);
        }
        dp[i][t][st][hash] = ans;
        return ans;
    }

    ll count(ll x) {
        string s = to_string(x);
        int n = s.size();
        vector<vector<vector<unordered_map<ll, ll>>>> dp(n, vector<vector<unordered_map<ll, ll>>>(2, vector<unordered_map<ll, ll>>(2)));
        return helper(0, true, false, 0, 1LL, s, dp);
    }

    int beautifulNumbers(int l, int r) {
        return count(r) - count(l - 1);
    }
};

