class Solution {
public:
    typedef long long ll;
    string numStr;
    unordered_map<ll, ll> memo;

    // Lambda function to calculate the product of prime factors

    ll dfs(int pos, bool tight, bool started, bool hasZero, int sum, int cnt2, int cnt3, int cnt5, int cnt7) {
         auto calculateProduct = [](int cnt2, int cnt3, int cnt5, int cnt7) -> int {
            return pow(2, cnt2) * pow(3, cnt3) * pow(5, cnt5) * pow(7, cnt7);
        };

        // Lambda function to factorize a digit and update prime counts
        auto factorizeDigit = [](int digit, int& cnt2, int& cnt3, int& cnt5, int& cnt7) {
            while (digit % 2 == 0) { cnt2++; digit /= 2; }
            while (digit % 3 == 0) { cnt3++; digit /= 3; }
            while (digit % 5 == 0) { cnt5++; digit /= 5; }
            while (digit % 7 == 0) { cnt7++; digit /= 7; }
        };

        if (pos == numStr.size()) {
            if (!started) return 0; // No number formed
            if (hasZero) return 1; // Number contains zero, valid
            int product = calculateProduct(cnt2, cnt3, cnt5, cnt7); // Calculate product using lambda
            return (sum != 0 && product % sum == 0) ? 1LL : 0LL; // Check divisibility
        }

        // Generate a unique key for memoization
        ll key = pos;
        key |= ((ll)tight << 4);
        key |= ((ll)started << 5);
        key |= ((ll)hasZero << 6);
        key |= ((ll)sum << 7);
        key |= ((ll)cnt2 << (7 + 7));
        key |= ((ll)cnt3 << (7 + 7 + 6));
        key |= ((ll)cnt5 << (7 + 7 + 6 + 6));
        key |= ((ll)cnt7 << (7 + 7 + 6 + 6 + 5));

        if (memo.find(key) != memo.end()) return memo[key]; // Return cached result

        ll result = 0;
        int limit = tight ? numStr[pos] - '0' : 9; // Determine the digit limit

        for (int digit = 0; digit <= limit; digit++) {
            bool newTight = tight && (digit == limit); // Update tight flag

            if (!started) {
                if (digit == 0) {
                    // Skip leading zeros
                    result += dfs(pos + 1, newTight, false, false, 0, 0, 0, 0, 0);
                } else {
                    // Start forming the number
                    int newSum = digit;
                    int newCnt2 = 0, newCnt3 = 0, newCnt5 = 0, newCnt7 = 0;
                    factorizeDigit(digit, newCnt2, newCnt3, newCnt5, newCnt7); // Factorize using lambda
                    result += dfs(pos + 1, newTight, true, false, newSum, newCnt2, newCnt3, newCnt5, newCnt7);
                }
            } else {
                if (hasZero) {
                    // If the number already has a zero, just add the digit to the sum
                    int newSum = sum + digit;
                    result += dfs(pos + 1, newTight, true, true, newSum, 0, 0, 0, 0);
                } else {
                    if (digit == 0) {
                        // Add zero to the number
                        int newSum = sum;
                        result += dfs(pos + 1, newTight, true, true, newSum, 0, 0, 0, 0);
                    } else {
                        // Add non-zero digit and update prime counts
                        int newSum = sum + digit;
                        int newCnt2 = cnt2, newCnt3 = cnt3, newCnt5 = cnt5, newCnt7 = cnt7;
                        factorizeDigit(digit, newCnt2, newCnt3, newCnt5, newCnt7); // Factorize using lambda
                        result += dfs(pos + 1, newTight, true, false, newSum, newCnt2, newCnt3, newCnt5, newCnt7);
                    }
                }
            }
        }

        memo[key] = result; // Cache the result
        return result;
    }

    ll countBeautifulNumbers(ll x) {
        if (x < 1) return 0; // Edge case
        numStr = to_string(x); // Convert number to string
        memo.clear(); // Clear memoization cache
        return dfs(0, true, false, false, 0, 0, 0, 0, 0); // Start DFS
    }

    int beautifulNumbers(int l, int r) {
        // Calculate the difference between counts for [1, r] and [1, l-1]
        return (int)(countBeautifulNumbers(r) - countBeautifulNumbers((ll)l - 1));
    }
};

