from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n: int) -> int:
            if n == 0:
                return 0

            digits = list(map(int, str(n)))
            length = len(digits)

            @lru_cache(None)
            def dp(pos: int, tight: bool, digit_sum: int, digit_product: int, leading_zero: bool) -> int:
                if pos == length:
                    return 1 if digit_sum != 0 and digit_product % digit_sum == 0 else 0

                limit = digits[pos] if tight else 9
                res = 0

                for d in range(0, limit + 1):
                    new_tight = tight and (d == limit)
                    new_digit_sum = digit_sum + d
                    new_digit_product = digit_product * d if d != 0 or not leading_zero else 1
                    res += dp(pos + 1, new_tight, new_digit_sum, new_digit_product, leading_zero and d == 0)

                return res

            return dp(0, True, 0, 1, True)

        return count_beautiful(r) - count_beautiful(l - 1)

# Example usage:
sol = Solution()
print(sol.beautifulNumbers(10, 20))  # Output: 2
print(sol.beautifulNumbers(1, 15))   # Output: 10

