class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def solve(n: int) -> int:
            ns = str(n)
            @cache
            def dp(p, t, st, s, pr, ns):
                if p == len(ns):
                    return int(st and s != 0 and pr % s == 0)
                lm = int(ns[p]) if t else 9
                tot = 0
                for d in range(lm + 1):
                    tot += dp(p + 1, t and (d == lm), not (not st and d == 0), s + (0 if (not st and d == 0) else d), pr * (1 if (not st and d == 0) else d), ns)
                return tot
            return dp(0, True, False, 0, 1, ns)
        return solve(r)-solve(l-1)

