class Solution {
public:
    int beautifulNumbers(int l, int r) {

        auto helper = [&](ll num) -> ll {
            string str = to_string(num);
            ll n = sz(str);
            map<tuple<ll, ll, ll, ll, ll>, ll> dp;
            dp[{1, 0, 0, 0, 1}] = 1;
            loop(pos, n) {
                map<tuple<ll, ll, ll, ll, ll>, ll> next_dp;
                trav(u, dp) {
                    auto [a, b, c, p, q] = u.ff;

                    ll dig = a ? (str[pos] - '0') : 9;
                    loop(d, dig + 1) {
                        ll aa = (a && (d == dig));
                        ll bb = b;
                        ll cc = c;
                        ll pp = p;
                        ll qq = q;

                        if (!b) {
                            if (d == 0) {
                                bb = 0;
                                cc = 0;
                                pp = 0;
                                qq = 1;
                            } else {
                                bb = 1;
                                cc = 0;
                                pp = d;
                                qq = d;
                            }
                        } else {
                            pp += d;
                            if (d == 0) {
                                cc = 1;
                                qq = 0;
                            } else {
                                qq *= d;
                            }
                        }

                        next_dp[{aa, bb, cc, pp, qq}] += u.ss;
                    }
                }
                dp = next_dp;
            }

            ll ans = 0;
            trav(u, dp) {
                auto [a, b, c, p, q] = u.ff;
                if (b) {
                    if (c) {
                        ans += u.ss;
                    } else {
                        if (p != 0 and q % p == 0) {
                            ans += u.ss;
                        }
                    }
                }
            }
            return ans;
        };

        ll rb = helper(r);
        if(l <= 1) {
            ll ans = helper(r);
            return ans;
        }

        ll lb = helper(l - 1);
        return rb - lb;
    }
};

