class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        MOD = 10 ** 9 + 7

        @cache
        def dp(cur_sum, cur_prod, digit_loc, isNum, isLimit, max_num):
            if digit_loc == len(max_num):
                return 1 if isNum and cur_sum > 0 and cur_prod % cur_sum == 0 else 0
            subAns = 0
            up = int(max_num[digit_loc]) if isLimit else 9
            low = 0 if isNum else 1
            if not isNum:
                subAns += dp(0, 1, digit_loc + 1, False, False, max_num)
            for i in range(low, up + 1):
                subAns += dp(cur_sum + i, cur_prod * i, digit_loc + 1, True, isLimit and i == up, max_num)
            return subAns % MOD
        return (dp(0, 1, 0, False, True, str(r)) - dp(0, 1, 0, False, True, str(l - 1))) % MOD

