class Solution {
public:
    int dp[10][82][82][2][2];
    vector<int> digits;

    int digitDP(int pos, int currSum, int currProd, int tight, int targetSum, int leadingZero) {
        if (pos == digits.size()) {
            return (!leadingZero && currSum == targetSum && currProd % targetSum == 0) ? 1 : 0;
        }
        if (dp[pos][currSum][currProd][tight][leadingZero] != -1) return dp[pos][currSum][currProd][tight][leadingZero];

        int limit = tight ? digits[pos] : 9, res = 0;
        for (int d = 0; d <= limit; ++d) {
            int newTight = (tight && d == limit);
            int newLeadingZero = (leadingZero && d == 0);
            int newSum = currSum, newProd = currProd;
            if (!newLeadingZero) {
                newSum += d;
                if (newSum > targetSum) continue;
                newProd = (currProd * d) % targetSum;
            }
            res += digitDP(pos + 1, newSum, newProd, newTight, targetSum, newLeadingZero);
        }
        return dp[pos][currSum][currProd][tight][leadingZero] = res;
    }

    int countBeautiful(int n) {
        if (n == 0) return 0;
        digits.clear();
        while (n) {
            digits.push_back(n % 10);
            n /= 10;
        }
        reverse(digits.begin(), digits.end());
        int total = 0;
        for (int sum = 1; sum <= 81; ++sum) {
            memset(dp, -1, sizeof(dp));
            total += digitDP(0, 0, 1, 1, sum, 1);
        }
        return total;
    }

    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }
};

