class Solution {
public:
    int beautifulNumbers(int l, int r) {
        if (l > 1) return beautifulNumbers(1, r) - beautifulNumbers(1, l - 1);
        if (r <= 10) return r;
        pastri = PascalTriangle(9);
        int ans = 0;
        string sr = to_string(r);
        int len = sr.length();
        for (int i = 1; i < len; ++i) {
            ans += count(i, 1, 0, false);
        }
        int p = 1, s = 0;
        for (int i = 0; i < len; ++i) {
            int digit = sr[i] - '0';
            for (int j = (i == 0 ? 1 : 0); j <= (i == len - 1 ? digit : digit - 1); ++j) {
                ans += count(len - i - 1, p * j, s + j, true);
            }
            p *= digit;
            s += digit;
        }
        return ans;
    }
private:
    vector<vector<int>> pastri;

    vector<vector<int>> PascalTriangle(int n) {
        vector<vector<int>> pascal_triangle;
        vector<int> row;
        for (int i = 0; i <= n; ++i) {
            row.push_back(1);
            for (int j = i - 1; j > 0; --j) {
                row[j] = row[j - 1] + row[j];
            }
            pascal_triangle.push_back(row);
        }
        return pascal_triangle;
    }

    vector<vector<int>> cnts;

    void recursion(int d, int max_cnt, vector<int>& cnt) {
        if (d == 9) {
            cnt[9] = max_cnt;
            cnts.push_back(cnt);
        } else {
            for (int i = 0; i <= max_cnt; ++i) {
                cnt[d] = i;
                recursion(d + 1, max_cnt - i, cnt);
            }
        }
    }

    int getNumPermutations(int len, const vector<int>& cnt) {
        int ret = 1;
        for (int i = 0; i < 10; ++i) {
            ret *= pastri[len][cnt[i]];
            len -= cnt[i];
        }
        return ret;
    }

    int count(int len, int product, int sum, bool lzero) {
        int ret = 0;
        if (len == 0) {
            ret = product % sum == 0 ? 1 : 0;
        } else {
            cnts.clear();
            vector<int> tcnt(10);
            recursion(0, len, tcnt);
            for (vector<int>& cnt : cnts) {
                int extra_product = 1, extra_sum = 0;
                for (int i = 0; i < 10; ++i) {
                    for (int j = 0; j < cnt[i]; ++j) {
                        extra_product *= i;
                        extra_sum += i;
                    }
                }
                if (sum + extra_sum == 0 || (product * extra_product) % (sum + extra_sum) != 0) continue;
                ret += getNumPermutations(len, cnt);
                if (!lzero && cnt[0] > 0) {
                    --cnt[0];
                    ret -= getNumPermutations(len - 1, cnt);
                }
            }
        }
        return ret;
    }
};

