class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def is_beautiful(num):
            digits = [int(d) for d in str(num)]
            sum_digits = sum(digits)
            product_digits = 1
            for d in digits:
                product_digits *= d
            return sum_digits != 0 and product_digits % sum_digits == 0

        def count_up_to(n):
            s = str(n)
            length = len(s)
            from functools import lru_cache

            @lru_cache(maxsize=None)
            def dp(pos, tight, sum_digits, product_digits, has_started):
                if pos == length:
                    return int(has_started and sum_digits != 0 and product_digits % sum_digits == 0)

                limit = int(s[pos]) if tight else 9
                total = 0

                for digit in range(0, limit + 1):
                    new_tight = tight and (digit == limit)
                    new_sum = sum_digits + digit
                    new_product = product_digits * digit if has_started or digit != 0 else 1
                    new_has_started = has_started or (digit != 0)

                    total += dp(pos + 1, new_tight, new_sum, new_product, new_has_started)

                return total

            return dp(0, True, 0, 1, False)

        return count_up_to(r) - count_up_to(l - 1)

