from functools import lru_cache

class Solution:
    def countBeautiful(self, x: int) -> int:
        if x < 1:
            return 0

        s = str(x)

        @lru_cache(None)
        def rec(pos, tight, started, hasZero, sum_, e2, e3, e5, e7):
            if pos == len(s):
                if not started:
                    return 0
                if hasZero:
                    return 1
                prod = (2 ** e2) * (3 ** e3) * (5 ** e5) * (7 ** e7)
                return 1 if sum_ != 0 and prod % sum_ == 0 else 0

            res = 0
            limit = int(s[pos]) if tight else 9

            for d in range(limit + 1):
                ntight = tight and (d == limit)
                if not started:
                    if d == 0:
                        res += rec(pos + 1, ntight, 0, 0, 0, 0, 0, 0, 0)
                    else:
                        new_e2, new_e3, new_e5, new_e7 = 0, 0, 0, 0
                        temp = d
                        while temp % 2 == 0:
                            new_e2 += 1
                            temp //= 2
                        while temp % 3 == 0:
                            new_e3 += 1
                            temp //= 3
                        while temp % 5 == 0:
                            new_e5 += 1
                            temp //= 5
                        while temp % 7 == 0:
                            new_e7 += 1
                            temp //= 7
                        res += rec(pos + 1, ntight, 1, 0, d, new_e2, new_e3, new_e5, new_e7)
                else:
                    if hasZero:
                        res += rec(pos + 1, ntight, 1, 1, sum_ + d, 0, 0, 0, 0)
                    else:
                        if d == 0:
                            res += rec(pos + 1, ntight, 1, 1, sum_, 0, 0, 0, 0)
                        else:
                            new_e2, new_e3, new_e5, new_e7 = e2, e3, e5, e7
                            temp = d
                            while temp % 2 == 0:
                                new_e2 += 1
                                temp //= 2
                            while temp % 3 == 0:
                                new_e3 += 1
                                temp //= 3
                            while temp % 5 == 0:
                                new_e5 += 1
                                temp //= 5
                            while temp % 7 == 0:
                                new_e7 += 1
                                temp //= 7
                            res += rec(pos + 1, ntight, 1, 0, sum_ + d, new_e2, new_e3, new_e5, new_e7)

            return res

        return rec(0, 1, 0, 0, 0, 0, 0, 0, 0)

    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.countBeautiful(r) - self.countBeautiful(l - 1)

