import java.util.*;

class Solution {
    public int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }

    // Returns the count of beautiful numbers in [1, x]. (0 is not counted.)
    private int countBeautiful(int x) {
        if (x < 1) return 0;
        char[] digits = String.valueOf(x).toCharArray();
        Map<State, Integer> memo = new HashMap<>();
        return dp(digits, 0, true, false, 0, 1L, memo);
    }

    // DP function: returns count of beautiful numbers for current state.
    // Parameters:
    //  digits: digit array of the upper bound number
    //  pos: current position in digits array
    //  tight: true if digits chosen so far are on the prefix of x
    //  started: true if we have already picked a non-zero digit
    //  sum: sum of digits chosen so far
    //  prod: product of digits chosen so far (if not started, prod is 1)
    private int dp(char[] digits, int pos, boolean tight, boolean started, int sum, long prod, Map<State, Integer> memo) {
        if (pos == digits.length) {
            // Only count numbers that have started (exclude the '0' number).
            if (!started) return 0;
            // Guard: if for some reason sum is 0 (shouldn't happen as started==true implies at least one nonzero digit)
            if (sum == 0) return 0;
            return (prod % sum == 0) ? 1 : 0;
        }

        State key = new State(pos, tight, started, sum, prod);
        if (memo.containsKey(key)) return memo.get(key);

        int res = 0;
        int limit = tight ? digits[pos] - '0' : 9;

        for (int d = 0; d <= limit; d++) {
            boolean ntight = tight && (d == limit);
            boolean nstarted = started || (d != 0);
            int nsum = nstarted ? sum + d : sum; 
            // If we haven't started, prod remains 1; otherwise update (note: if d==0 then nprod becomes 0).
            long nprod = nstarted ? prod * d : prod;
            res += dp(digits, pos + 1, ntight, nstarted, nsum, nprod, memo);
        }

        memo.put(key, res);
        return res;
    }

    // Helper class representing the DP state.
    private static class State {
        int pos;
        boolean tight;
        boolean started;
        int sum;
        long prod;

        public State(int pos, boolean tight, boolean started, int sum, long prod) {
            this.pos = pos;
            this.tight = tight;
            this.started = started;
            this.sum = sum;
            this.prod = prod;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (!(o instanceof State)) return false;
            State state = (State) o;
            return pos == state.pos && tight == state.tight && started == state.started &&
                   sum == state.sum && prod == state.prod;
        }

        @Override
        public int hashCode() {
            return Objects.hash(pos, tight, started, sum, prod);
        }
    }
}

