var beautifulNumbers = function(l, r) {
    function countBeautiful(upper) {
        let total = 0
        for (let sum = 1; sum <= 81; sum++) {
            total += countBeautifulSum(upper, sum)
        }
        return total
    }
    function countBeautifulSum(upper, DSUM) {
        const digits = getDigits(upper);
        const n = digits.length;
        const memo = new Map();

        const dp = (pos, tight, sum, product, leading) => {
            if (sum > DSUM) return 0
            if (pos === n) {
                return (sum === DSUM && product % sum === 0) ? 1 : 0;
            }

            const key = +leading + product * 10 + sum * 1e3 + tight * 1e5 + pos * 1e6;
            if (memo.has(key)) return memo.get(key);

            let count = leading ? dp(pos + 1, false, sum, product, true) : 0;
            const lo = leading ? 1 : 0;
            const hi = tight ? digits[pos] : 9;

            for (let d = lo; d <= hi; d++) {
                const newSum = sum + d;
                const newProduct = product * d % DSUM;
                const newTight = tight && (d === digits[pos]);
                count += dp(pos + 1, newTight, newSum, newProduct, false);
            }

            memo.set(key, count);
            return count;
        };
        // console.log(`countBeaultifulSum(${upper}, ${DSUM}): ${dp(0, true, 0, 1, true)}`)
        return dp(0, true, 0, 1, true);
    };
    return countBeautiful(r) - countBeautiful(l - 1);
};


function getDigits(num) {
    const digits = [];
    while (num > 0) {
        digits.unshift(num % 10);
        num = Math.floor(num / 10);
    }
    return digits.length > 0 ? digits : [0];
}

