class Solution {
    private Map<String, Long> memo;
    private char[] digits;

    private long dp(int pos, boolean tight, boolean started, boolean hasZero, int sum, int product) {
        if (pos == digits.length) {
            if (!started) return 0;
            if (hasZero) return 1;
            return (sum != 0 && product % sum == 0) ? 1 : 0;
        }

        String key = pos + "_" + tight + "_" + started + "_" + hasZero + "_" + sum + "_" + product;
        if (memo.containsKey(key))
            return memo.get(key);

        long count = 0;
        int limit = tight ? digits[pos] - '0' : 9;

        for (int d = 0; d <= limit; d++) {
            boolean newTight = tight && (d == limit);
            boolean newStarted = started || (d != 0);
            boolean newHasZero = hasZero;
            int newSum = sum;
            int newProduct = product;

            if (!newStarted) {
                newSum = 0;
                newProduct = 1;
            } else {
                newSum = sum + d;
                if (hasZero || d == 0) {
                    newHasZero = true;
                    newProduct = 0;
                } else {
                    if (!started) {
                        newProduct = d;
                    } else {
                        newProduct = product * d;
                    }
                }
            }

            count += dp(pos + 1, newTight, newStarted, newHasZero, newSum, newProduct);
        }

        memo.put(key, count);
        return count;
    }

    private long countBeautiful(String num) {
        digits = num.toCharArray();
        memo = new HashMap<>();
        return dp(0, true, false, false, 0, 1);
    }

    public int beautifulNumbers(int l, int r) {
        long countR = countBeautiful(String.valueOf(r));
        long countL = countBeautiful(String.valueOf(l - 1));
        return (int)(countR - countL);
    }
}

