class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n: int) -> int:
            if n == 0:
                return 0  # No valid numbers below 1

            digits = list(map(int, str(n)))  # Convert number to a digit array

            @lru_cache(None)
            def dp(pos: int, sum_digits: int, product_digits: int, tight: bool, leading_zero: bool) -> int:
                if pos == len(digits):  
                    return 1 if sum_digits > 0 and product_digits % sum_digits == 0 else 0

                limit = digits[pos] if tight else 9  # If tight, respect digit limit
                result = 0

                for digit in range(0, limit + 1):
                    new_product = product_digits * digit if digit != 0 or not leading_zero else 1  # Avoid zero multiplication issue
                    result += dp(
                        pos + 1,
                        sum_digits + digit,
                        new_product,
                        tight and (digit == limit),
                        leading_zero and (digit == 0)
                    )
                return result

            return dp(0, 0, 1, True, True)  # Start recursion

        return count_beautiful(r) - count_beautiful(l - 1)

