function beautifulNumbers(L, R) {
  if (L > R) return 0;
  return countBeautiful(R) - countBeautiful(L - 1);
}

function countBeautiful(x) {
  if (x < 1) return 0;
  let total = x;
  let noZero = countNoZero(x);
  let noZeroDiv = countNoZeroDivisible(x);
  return total - noZero + noZeroDiv;
}

function countNoZero(x) {
  if (x < 1) return 0;
  let d = getDigits(x);
  let n = d.length;
  const DP_SIZE = (n + 1) * 2 * 2;
  let dp = new Int32Array(DP_SIZE).fill(-1);

  function idx(pos, leading, tight) {
    return (pos << 2) | (leading << 1) | tight;
  }

  function solve(pos, leading, tight) {
    if (pos === n) {
      return leading ? 0 : 1;
    }
    let dpIndex = idx(pos, leading ? 1 : 0, tight ? 1 : 0);
    let memoVal = dp[dpIndex];
    if (memoVal !== -1) return memoVal;

    let limit = tight ? d[pos] : 9;
    let ways = 0;
    for (let dig = 0; dig <= limit; dig++) {
      if (!leading && dig === 0) continue;
      let nLeading = leading && dig === 0;
      let nTight = tight && dig === limit;
      ways += solve(pos + 1, nLeading, nTight);
    }
    dp[dpIndex] = ways;
    return ways;
  }

  return solve(0, true, true);
}

function countNoZeroDivisible(x) {
  if (x < 1) return 0;
  let digits = getDigits(x);
  let n = digits.length;
  let maxSum = 9 * n;
  let ans = 0;

  for (let S = 1; S <= maxSum; S++) {
    ans += countNoZeroDivSum(digits, S);
  }
  return ans;
}

function countNoZeroDivSum(d, S) {
  let n = d.length;
  const SUM_SIZE = S + 1;
  const REM_SIZE = S;
  const DP_SIZE = n * SUM_SIZE * REM_SIZE * 2 * 2;
  let dp = new Int32Array(DP_SIZE).fill(-1);

  function idx(pos, sumSoFar, rem, started, tight) {
    let stride1 = SUM_SIZE * REM_SIZE * 4;
    let stride2 = REM_SIZE * 4;
    let stride3 = 4;
    return (
      pos * stride1 +
      sumSoFar * stride2 +
      rem * stride3 +
      (started << 1) +
      tight
    );
  }

  function solve(pos, sumSoFar, rem, started, tight) {
    if (pos === n) {
      if (!started) return 0;
      return sumSoFar === S && rem === 0 ? 1 : 0;
    }
    if (sumSoFar > S) {
      return 0;
    }
    let dpIndex = idx(pos, sumSoFar, rem, started ? 1 : 0, tight ? 1 : 0);
    let memoVal = dp[dpIndex];
    if (memoVal !== -1) return memoVal;

    let limit = tight ? d[pos] : 9;
    let ways = 0;

    for (let dig = 0; dig <= limit; dig++) {
      if (started && dig === 0) continue;

      let nStarted = started,
        nSum = sumSoFar,
        nRem = rem,
        nTight = tight && dig === limit;
      if (!nStarted) {
        if (dig > 0) {
          nStarted = true;
          nSum += dig;
          if (nSum > S) break;
          nRem = dig % S;
        }
      } else {
        nSum += dig;
        if (nSum > S) break;
        nRem = (nRem * dig) % S;
      }
      ways += solve(pos + 1, nSum, nRem, nStarted, nTight);
    }

    dp[dpIndex] = ways;
    return ways;
  }

  return solve(0, 0, 0, false, true);
}

function getDigits(num) {
  return String(num).split("").map(Number);
}

